import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { jobApi } from '../services/api';
import toast from 'react-hot-toast';

const CreateJob = () => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [formData, setFormData] = useState({
    taskName: '',
    priority: 'Medium',
    payload: '{}'
  });
  const [payloadError, setPayloadError] = useState('');

  const priorities = [
    { value: 'Low', label: 'Low', color: 'bg-green-100 text-green-800' },
    { value: 'Medium', label: 'Medium', color: 'bg-yellow-100 text-yellow-800' },
    { value: 'High', label: 'High', color: 'bg-red-100 text-red-800' }
  ];

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
    
    // Validate JSON when payload changes
    if (name === 'payload') {
      try {
        JSON.parse(value);
        setPayloadError('');
      } catch (error) {
        setPayloadError('Invalid JSON format');
      }
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!formData.taskName.trim()) {
      toast.error('Task name is required');
      return;
    }
    
    if (payloadError) {
      toast.error('Please fix JSON errors');
      return;
    }
    
    try {
      setLoading(true);
      
      const jobData = {
        taskName: formData.taskName,
        priority: formData.priority,
        payload: JSON.parse(formData.payload || '{}')
      };
      
      await jobApi.createJob(jobData);
      
      toast.success('Job created successfully!');
      navigate('/dashboard');
    } catch (error) {
      console.error('Error creating job:', error);
      toast.error(error.response?.data?.error || 'Failed to create job');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-2xl mx-auto">
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900">Create New Job</h1>
        <p className="text-gray-600 mt-2">Schedule a new background task</p>
      </div>

      <div className="bg-white rounded-lg shadow p-6">
        <form onSubmit={handleSubmit} className="space-y-6">
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Task Name *
            </label>
            <input
              type="text"
              name="taskName"
              value={formData.taskName}
              onChange={handleChange}
              className="w-full px-3 py-2 border rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
              placeholder="e.g., Send Email, Generate Report"
              required
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Priority
            </label>
            <div className="flex space-x-4">
              {priorities.map((priority) => (
                <button
                  key={priority.value}
                  type="button"
                  onClick={() => setFormData(prev => ({ ...prev, priority: priority.value }))}
                  className={`px-4 py-2 rounded-lg border transition ${
                    formData.priority === priority.value
                      ? `${priority.color} border-transparent`
                      : 'border-gray-300 hover:bg-gray-50'
                  }`}
                >
                  {priority.label}
                </button>
              ))}
            </div>
          </div>

          <div>
            <div className="flex justify-between items-center mb-2">
              <label className="block text-sm font-medium text-gray-700">
                Payload (JSON)
              </label>
              <button
                type="button"
                onClick={() => {
                  setFormData(prev => ({ 
                    ...prev, 
                    payload: JSON.stringify({ example: "data" }, null, 2) 
                  }));
                  setPayloadError('');
                }}
                className="text-sm text-blue-600 hover:text-blue-800"
              >
                Add Example
              </button>
            </div>
            <textarea
              name="payload"
              value={formData.payload}
              onChange={handleChange}
              rows="8"
              className={`w-full px-3 py-2 border rounded-lg font-mono text-sm focus:ring-2 focus:ring-blue-500 focus:border-blue-500 ${
                payloadError ? 'border-red-300' : 'border-gray-300'
              }`}
              placeholder='{"key": "value"}'
            />
            {payloadError && (
              <p className="mt-1 text-sm text-red-600">{payloadError}</p>
            )}
            <p className="mt-1 text-sm text-gray-500">
              Optional JSON data needed for job execution
            </p>
          </div>

          <div className="flex justify-end space-x-4 pt-6 border-t">
            <button
              type="button"
              onClick={() => navigate('/dashboard')}
              className="px-4 py-2 border rounded-lg hover:bg-gray-50 transition"
            >
              Cancel
            </button>
            <button
              type="submit"
              disabled={loading || !!payloadError}
              className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition disabled:opacity-50 disabled:cursor-not-allowed"
            >
              {loading ? 'Creating...' : 'Create Job'}
            </button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default CreateJob;
