import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import toast from 'react-hot-toast';

const JobDetails = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [job, setJob] = useState(null);
  const [loading, setLoading] = useState(true);
  const [refreshing, setRefreshing] = useState(false);

  const fetchJob = async () => {
    try {
      const response = await fetch(`/api/jobs/${id}`);
      
      if (!response.ok) {
        throw new Error(`API Error: ${response.status}`);
      }
      
      const data = await response.json();
      setJob(data);
    } catch (error) {
      console.error('Error fetching job:', error);
      toast.error('Failed to load job details');
      navigate('/dashboard');
    } finally {
      setLoading(false);
      setRefreshing(false);
    }
  };

  useEffect(() => {
    fetchJob();
  }, [id]);

  const handleRunJob = async () => {
    if (!window.confirm('Are you sure you want to run this job?')) return;
    
    try {
      toast.loading('Starting job...');
      
      const response = await fetch(`/api/jobs/${id}/run`, {
        method: 'POST'
      });
      
      if (!response.ok) {
        throw new Error('Failed to run job');
      }
      
      const result = await response.json();
      
      toast.dismiss();
      toast.success('Job started successfully! Will complete in 3 seconds');
      
      // Start refreshing status
      setRefreshing(true);
      
      // Refresh immediately
      setTimeout(() => {
        fetchJob();
      }, 1000);
      
      // Refresh again after completion
      setTimeout(() => {
        fetchJob();
        setRefreshing(false);
      }, 4000);
      
    } catch (error) {
      toast.dismiss();
      toast.error(error.message || 'Failed to run job');
    }
  };

  const handleDeleteJob = async () => {
    if (!window.confirm('Are you sure you want to delete this job? This action cannot be undone.')) return;
    
    try {
      const response = await fetch(`/api/jobs/${id}`, {
        method: 'DELETE'
      });
      
      if (!response.ok) {
        throw new Error('Failed to delete job');
      }
      
      toast.success('Job deleted successfully');
      navigate('/dashboard');
    } catch (error) {
      toast.error(error.message || 'Failed to delete job');
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'pending': return 'bg-yellow-100 text-yellow-800';
      case 'running': return 'bg-blue-100 text-blue-800';
      case 'completed': return 'bg-green-100 text-green-800';
      case 'failed': return 'bg-red-100 text-red-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const getPriorityColor = (priority) => {
    switch (priority) {
      case 'High': return 'bg-red-100 text-red-800';
      case 'Medium': return 'bg-yellow-100 text-yellow-800';
      case 'Low': return 'bg-green-100 text-green-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const formatDate = (dateString) => {
    if (!dateString) return 'N/A';
    const date = new Date(dateString);
    return date.toLocaleString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-[60vh]">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500 mx-auto"></div>
          <p className="mt-4 text-gray-600">Loading job details...</p>
        </div>
      </div>
    );
  }

  if (!job) {
    return (
      <div className="max-w-4xl mx-auto">
        <button
          onClick={() => navigate('/dashboard')}
          className="text-blue-600 hover:text-blue-800 mb-8 flex items-center"
        >
          ← Back to Dashboard
        </button>
        <div className="bg-white rounded-lg shadow p-8 text-center">
          <h2 className="text-2xl font-bold text-gray-900 mb-4">Job Not Found</h2>
          <p className="text-gray-600 mb-6">The job you're looking for doesn't exist or has been deleted.</p>
          <button
            onClick={() => navigate('/dashboard')}
            className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition"
          >
            Return to Dashboard
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="max-w-6xl mx-auto px-4">
      {/* Navigation */}
      <button
        onClick={() => navigate('/dashboard')}
        className="text-blue-600 hover:text-blue-800 mb-6 flex items-center"
      >
        ← Back to Dashboard
      </button>

      {/* Header */}
      <div className="flex flex-col md:flex-row md:items-center justify-between mb-8">
        <div>
          <h1 className="text-3xl font-bold text-gray-900">{job.taskName}</h1>
          <p className="text-gray-600 mt-2">Job ID: #{job.id}</p>
        </div>
        <div className="flex space-x-3 mt-4 md:mt-0">
          <button
            onClick={handleRunJob}
            disabled={job.status !== 'pending' || refreshing}
            className={`px-4 py-2 rounded-lg font-medium transition ${
              job.status === 'pending' && !refreshing
                ? 'bg-blue-600 text-white hover:bg-blue-700'
                : 'bg-gray-200 text-gray-500 cursor-not-allowed'
            }`}
          >
            {refreshing ? (
              <span className="flex items-center">
                <svg className="animate-spin -ml-1 mr-2 h-4 w-4 text-white" fill="none" viewBox="0 0 24 24">
                  <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4" />
                  <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z" />
                </svg>
                Running...
              </span>
            ) : (
              'Run Job'
            )}
          </button>
          <button
            onClick={handleDeleteJob}
            className="px-4 py-2 border border-red-600 text-red-600 rounded-lg hover:bg-red-50 transition"
          >
            Delete Job
          </button>
        </div>
      </div>

      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
        {/* Status Card */}
        <div className="bg-white rounded-lg shadow p-6">
          <div className="flex items-center justify-between mb-4">
            <h3 className="text-lg font-semibold text-gray-700">Status</h3>
            {refreshing && (
              <div className="animate-spin rounded-full h-4 w-4 border-t-2 border-b-2 border-blue-500"></div>
            )}
          </div>
          <div className="flex items-center">
            <span className={`px-3 py-1 rounded-full text-sm font-medium ${getStatusColor(job.status)}`}>
              {job.status.charAt(0).toUpperCase() + job.status.slice(1)}
            </span>
            <div className="ml-4">
              <p className="text-sm text-gray-500">Current State</p>
              {job.status === 'running' && (
                <p className="text-xs text-blue-600 mt-1">Processing... (3 seconds)</p>
              )}
              {job.status === 'completed' && (
                <p className="text-xs text-green-600 mt-1">✓ Successfully completed</p>
              )}
            </div>
          </div>
        </div>

        {/* Priority Card */}
        <div className="bg-white rounded-lg shadow p-6">
          <h3 className="text-lg font-semibold text-gray-700 mb-4">Priority</h3>
          <div className="flex items-center">
            <span className={`px-3 py-1 rounded-full text-sm font-medium ${getPriorityColor(job.priority)}`}>
              {job.priority}
            </span>
            <div className="ml-4">
              <p className="text-sm text-gray-500">Importance Level</p>
              {job.priority === 'High' && (
                <p className="text-xs text-red-600 mt-1">Urgent - Process first</p>
              )}
            </div>
          </div>
        </div>

        {/* Timestamps Card */}
        <div className="bg-white rounded-lg shadow p-6">
          <h3 className="text-lg font-semibold text-gray-700 mb-4">Timeline</h3>
          <div className="space-y-3">
            <div>
              <p className="text-xs text-gray-500">Created</p>
              <p className="text-sm font-medium">{formatDate(job.createdAt)}</p>
            </div>
            <div>
              <p className="text-xs text-gray-500">Last Updated</p>
              <p className="text-sm font-medium">{formatDate(job.updatedAt)}</p>
            </div>
            {job.completedAt && (
              <div>
                <p className="text-xs text-gray-500">Completed</p>
                <p className="text-sm font-medium text-green-600">{formatDate(job.completedAt)}</p>
              </div>
            )}
          </div>
        </div>
      </div>
// In the Timestamps card, add webhook status:
<div className="bg-white rounded-lg shadow p-6">
  <h3 className="text-lg font-semibold text-gray-700 mb-4">Timeline</h3>
  <div className="space-y-3">
    <div>
      <p className="text-xs text-gray-500">Created</p>
      <p className="text-sm font-medium">{formatDate(job.createdAt)}</p>
    </div>
    <div>
      <p className="text-xs text-gray-500">Last Updated</p>
      <p className="text-sm font-medium">{formatDate(job.updatedAt)}</p>
    </div>
    {job.completedAt && (
      <div>
        <p className="text-xs text-gray-500">Completed</p>
        <p className="text-sm font-medium text-green-600">{formatDate(job.completedAt)}</p>
      </div>
    )}
    {/* NEW: Webhook Status */}
    {job.status === 'completed' && (
      <div className="pt-3 border-t">
        <div className="flex items-center justify-between">
          <div>
            <p className="text-xs text-gray-500">Webhook Status</p>
            <p className={`text-sm font-medium ${job.webhookSent ? 'text-green-600' : 'text-yellow-600'}`}>
              {job.webhookSent ? '✓ Sent successfully' : '⏳ Not sent yet'}
            </p>
          </div>
          {!job.webhookSent && (
            <button
              onClick={async () => {
                try {
                  toast.loading('Resending webhook...');
                  const response = await fetch('/api/test-webhook', {
                    method: 'POST',
                    headers: { 'Content-Type': 'application/json' },
                    body: JSON.stringify({ jobId: job.id })
                  });
                  toast.dismiss();
                  if (response.ok) {
                    toast.success('Webhook sent successfully!');
                    fetchJob(); // Refresh job data
                  } else {
                    toast.error('Failed to send webhook');
                  }
                } catch (error) {
                  toast.dismiss();
                  toast.error('Error sending webhook');
                }
              }}
              className="px-3 py-1 text-xs bg-purple-100 text-purple-700 rounded hover:bg-purple-200"
            >
              Resend
            </button>
          )}
        </div>
      </div>
    )}
  </div>
</div>
      {/* Payload Section */}
      <div className="bg-white rounded-lg shadow p-6 mb-8">
        <div className="flex justify-between items-center mb-4">
          <h3 className="text-lg font-semibold text-gray-700">Payload Data</h3>
          {job.payload && Object.keys(job.payload).length > 0 && (
            <button
              onClick={() => {
                navigator.clipboard.writeText(JSON.stringify(job.payload, null, 2));
                toast.success('Payload copied to clipboard!');
              }}
              className="px-3 py-1 text-sm bg-gray-100 text-gray-700 rounded hover:bg-gray-200 transition"
            >
              Copy JSON
            </button>
          )}
        </div>
        
        {job.payload && Object.keys(job.payload).length > 0 ? (
          <div className="bg-gray-50 rounded-lg p-4 overflow-x-auto">
            <pre className="text-sm text-gray-800 font-mono">
              {JSON.stringify(job.payload, null, 2)}
            </pre>
          </div>
        ) : (
          <div className="bg-gray-50 rounded-lg p-8 text-center">
            <p className="text-gray-500">No payload data provided</p>
            <p className="text-sm text-gray-400 mt-2">This job doesn't require any additional data</p>
          </div>
        )}
        
        <p className="mt-4 text-sm text-gray-500">
          This JSON data is passed to the job when it runs. If webhooks are configured, this data will also be sent to the webhook endpoint upon completion.
        </p>
      </div>

      {/* Debug Info */}
      <div className="bg-gray-50 rounded-lg p-4 text-sm">
        <h4 className="font-medium text-gray-700 mb-2">Debug Information</h4>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-2">
          <div>
            <span className="text-gray-500">Job ID:</span>
            <span className="ml-2 font-mono">{job.id}</span>
          </div>
          <div>
            <span className="text-gray-500">API Endpoint:</span>
            <span className="ml-2 font-mono">/api/jobs/{job.id}</span>
          </div>
          <div>
            <span className="text-gray-500">Backend Status:</span>
            <span className="ml-2 text-green-600">✓ Connected</span>
          </div>
          <div>
            <span className="text-gray-500">Payload Size:</span>
            <span className="ml-2">
              {job.payload ? JSON.stringify(job.payload).length : 0} characters
            </span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default JobDetails;
