import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { jobApi } from '../services/api';
import toast from 'react-hot-toast';
import JobTable from '../components/JobTable';
import StatsCards from '../components/StatsCards';
import Filters from '../components/Filters';
import ActivityChart from '../components/ActivityChart';

const Dashboard = () => {
  const [jobs, setJobs] = useState([]);
  const [stats, setStats] = useState(null);
  const [loading, setLoading] = useState(true);
  const [filters, setFilters] = useState({
    status: '',
    priority: '',
    page: 1,
    limit: 10,
  });
  const [pagination, setPagination] = useState({});
  const navigate = useNavigate();

  const fetchData = async () => {
    try {
      setLoading(true);
      
      // Fetch jobs with filters
      const jobsResponse = await jobApi.getJobs(filters);
      setJobs(jobsResponse.data.jobs);
      setPagination(jobsResponse.data.pagination);
      
      // Fetch dashboard stats
      const statsResponse = await jobApi.getDashboardStats();
      setStats(statsResponse.data);
    } catch (error) {
      console.error('Error fetching data:', error);
      toast.error('Failed to load data');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchData();
  }, [filters]);

  const handleFilterChange = (newFilters) => {
    setFilters(prev => ({ ...prev, ...newFilters, page: 1 }));
  };

  const handlePageChange = (page) => {
    setFilters(prev => ({ ...prev, page }));
  };

  const handleRunJob = async (jobId) => {
    try {
      toast.loading('Starting job...');
      await jobApi.runJob(jobId);
      toast.dismiss();
      toast.success('Job started successfully');
      
      // Refresh data after a delay
      setTimeout(fetchData, 3500);
    } catch (error) {
      toast.dismiss();
      toast.error(error.response?.data?.error || 'Failed to run job');
    }
  };

  const handleDeleteJob = async (jobId) => {
    if (!window.confirm('Are you sure you want to delete this job?')) return;
    
    try {
      await jobApi.deleteJob(jobId);
      toast.success('Job deleted successfully');
      fetchData();
    } catch (error) {
      toast.error('Failed to delete job');
    }
  };

  if (loading && !stats) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <h1 className="text-3xl font-bold text-gray-900">Job Dashboard</h1>
        <button
          onClick={() => navigate('/jobs/create')}
          className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 transition"
        >
          + Create New Job
        </button>
      </div>

      {stats && (
        <>
          <StatsCards stats={stats.overview} />
          <ActivityChart data={stats.activity} />
        </>
      )}

      <div className="bg-white rounded-lg shadow p-6">
        <Filters 
          filters={filters} 
          onChange={handleFilterChange}
        />
        
        <JobTable
          jobs={jobs}
          onRunJob={handleRunJob}
          onDeleteJob={handleDeleteJob}
          loading={loading}
        />
        
        {pagination.totalPages > 1 && (
          <div className="flex items-center justify-between mt-4">
            <div className="text-sm text-gray-700">
              Showing page {pagination.page} of {pagination.totalPages}
            </div>
            <div className="flex space-x-2">
              <button
                onClick={() => handlePageChange(pagination.page - 1)}
                disabled={pagination.page === 1}
                className="px-3 py-1 rounded border disabled:opacity-50"
              >
                Previous
              </button>
              <button
                onClick={() => handlePageChange(pagination.page + 1)}
                disabled={pagination.page === pagination.totalPages}
                className="px-3 py-1 rounded border disabled:opacity-50"
              >
                Next
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
