import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import toast from 'react-hot-toast';

const Dashboard = () => {
  const [jobs, setJobs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [stats, setStats] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      setLoading(true);
      
      // Fetch jobs - handle both response formats
      const response = await fetch('/api/jobs');
      if (!response.ok) {
        throw new Error(`API Error: ${response.status}`);
      }
      
      const data = await response.json();
      
      // Handle different response formats:
      // 1. If data is an array, use it directly
      // 2. If data has a 'jobs' property, use that
      // 3. If data has a 'pagination' property, it's structured response
      if (Array.isArray(data)) {
        setJobs(data);
      } else if (data.jobs && Array.isArray(data.jobs)) {
        setJobs(data.jobs);
        // Don't try to access data.pagination if it doesn't exist
      } else {
        console.warn('Unexpected API response format:', data);
        setJobs([]);
      }
      
      // Try to fetch stats (optional)
      try {
        const statsResponse = await fetch('/api/jobs/stats/dashboard');
        if (statsResponse.ok) {
          const statsData = await statsResponse.json();
          setStats(statsData);
        }
      } catch (statsError) {
        console.log('Stats endpoint not available');
      }
      
    } catch (error) {
      console.error('Error fetching data:', error);
      toast.error('Failed to load data. Check if backend is running on port 5000');
      setJobs([]); // Set empty array on error
    } finally {
      setLoading(false);
    }
  };

  const handleCreateJob = () => {
    navigate('/jobs/create');
  };

  const handleRunJob = async (jobId) => {
    try {
      toast.loading('Starting job...');
      const response = await fetch(`/api/jobs/${jobId}/run`, {
        method: 'POST'
      });
      
      if (!response.ok) {
        throw new Error('Failed to run job');
      }
      
      toast.dismiss();
      toast.success('Job started successfully!');
      
      // Refresh after 3 seconds
      setTimeout(fetchData, 3000);
    } catch (error) {
      toast.dismiss();
      toast.error(error.message || 'Failed to run job');
    }
  };

  const handleViewDetails = (jobId) => {
    navigate(`/jobs/${jobId}`);
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'pending': return 'bg-yellow-100 text-yellow-800';
      case 'running': return 'bg-blue-100 text-blue-800';
      case 'completed': return 'bg-green-100 text-green-800';
      case 'failed': return 'bg-red-100 text-red-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const getPriorityColor = (priority) => {
    switch (priority) {
      case 'High': return 'bg-red-100 text-red-800';
      case 'Medium': return 'bg-yellow-100 text-yellow-800';
      case 'Low': return 'bg-green-100 text-green-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-[60vh]">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500 mx-auto"></div>
          <p className="mt-4 text-gray-600">Loading dashboard...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex justify-between items-center">
        <div>
          <h1 className="text-3xl font-bold text-gray-900">Job Dashboard</h1>
          <p className="text-gray-600 mt-1">Manage and monitor your automated jobs</p>
        </div>
        <button
          onClick={handleCreateJob}
          className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition"
        >
          + Create New Job
        </button>
      </div>

      {/* Stats Cards - Only show if stats exist */}
      {stats && stats.overview && (
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
          <div className="bg-white rounded-lg shadow p-6">
            <h3 className="text-lg font-semibold text-gray-700">Total Jobs</h3>
            <p className="text-3xl font-bold text-gray-900 mt-2">{stats.overview.totalJobs || jobs.length}</p>
          </div>
          <div className="bg-white rounded-lg shadow p-6">
            <h3 className="text-lg font-semibold text-gray-700">Pending</h3>
            <p className="text-3xl font-bold text-yellow-600 mt-2">{stats.overview.pendingJobs || 0}</p>
          </div>
          <div className="bg-white rounded-lg shadow p-6">
            <h3 className="text-lg font-semibold text-gray-700">Running</h3>
            <p className="text-3xl font-bold text-blue-600 mt-2">{stats.overview.runningJobs || 0}</p>
          </div>
          <div className="bg-white rounded-lg shadow p-6">
            <h3 className="text-lg font-semibold text-gray-700">Completed</h3>
            <p className="text-3xl font-bold text-green-600 mt-2">{stats.overview.completedJobs || 0}</p>
          </div>
        </div>
      )}

      {/* Simple Stats if no stats endpoint */}
      {(!stats || !stats.overview) && jobs.length > 0 && (
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
          <div className="bg-white rounded-lg shadow p-6">
            <h3 className="text-lg font-semibold text-gray-700">Total Jobs</h3>
            <p className="text-3xl font-bold text-gray-900 mt-2">{jobs.length}</p>
          </div>
          <div className="bg-white rounded-lg shadow p-6">
            <h3 className="text-lg font-semibold text-gray-700">Pending</h3>
            <p className="text-3xl font-bold text-yellow-600 mt-2">
              {jobs.filter(job => job.status === 'pending').length}
            </p>
          </div>
          <div className="bg-white rounded-lg shadow p-6">
            <h3 className="text-lg font-semibold text-gray-700">Running</h3>
            <p className="text-3xl font-bold text-blue-600 mt-2">
              {jobs.filter(job => job.status === 'running').length}
            </p>
          </div>
          <div className="bg-white rounded-lg shadow p-6">
            <h3 className="text-lg font-semibold text-gray-700">Completed</h3>
            <p className="text-3xl font-bold text-green-600 mt-2">
              {jobs.filter(job => job.status === 'completed').length}
            </p>
          </div>
        </div>
      )}

      {/* Jobs Table */}
      <div className="bg-white rounded-lg shadow p-6">
        <div className="flex justify-between items-center mb-6">
          <h2 className="text-xl font-semibold text-gray-800">All Jobs</h2>
          <button
            onClick={fetchData}
            className="px-4 py-2 bg-gray-200 text-gray-800 rounded-lg hover:bg-gray-300 transition"
          >
            Refresh
          </button>
        </div>

        {jobs.length === 0 ? (
          <div className="text-center py-12">
            <p className="text-gray-500 text-lg">No jobs found</p>
            <p className="text-gray-400 text-sm mt-2">
              Make sure your backend API is returning data from /api/jobs
            </p>
            <button
              onClick={handleCreateJob}
              className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition mt-4"
            >
              Create Your First Job
            </button>
          </div>
        ) : (
          <div className="overflow-x-auto">
            <table className="min-w-full divide-y divide-gray-200">
              <thead className="bg-gray-50">
                <tr>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Task</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Priority</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Status</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Created</th>
                  <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Actions</th>
                </tr>
              </thead>
              <tbody className="bg-white divide-y divide-gray-200">
                {jobs.map((job) => (
                  <tr key={job.id} className="hover:bg-gray-50">
                    <td className="px-6 py-4">
                      <div>
                        <div className="font-medium text-gray-900">{job.taskName}</div>
                        <div className="text-sm text-gray-500">ID: {job.id}</div>
                      </div>
                    </td>
                    <td className="px-6 py-4">
                      <span className={`px-2 py-1 text-xs rounded-full ${getPriorityColor(job.priority)}`}>
                        {job.priority}
                      </span>
                    </td>
                    <td className="px-6 py-4">
                      <span className={`px-2 py-1 text-xs rounded-full ${getStatusColor(job.status)}`}>
                        {job.status}
                      </span>
                    </td>
                    <td className="px-6 py-4 text-sm text-gray-500">
                      {new Date(job.createdAt).toLocaleDateString()}
                    </td>
                    <td className="px-6 py-4">
                      <div className="flex space-x-2">
                        <button
                          onClick={() => handleRunJob(job.id)}
                          disabled={job.status !== 'pending'}
                          className={`px-3 py-1 text-sm rounded ${
                            job.status === 'pending'
                              ? 'bg-blue-600 text-white hover:bg-blue-700'
                              : 'bg-gray-200 text-gray-500 cursor-not-allowed'
                          }`}
                        >
                          Run
                        </button>
                        <button
                          onClick={() => handleViewDetails(job.id)}
                          className="px-3 py-1 text-sm bg-gray-200 text-gray-800 rounded hover:bg-gray-300"
                        >
                          Details
                        </button>
                      </div>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
// In the stats cards section, add:
<div className="grid grid-cols-1 md:grid-cols-5 gap-4">
  {/* Existing stats cards */}
  <div className="bg-white rounded-lg shadow p-6">
    <h3 className="text-lg font-semibold text-gray-700">Total Jobs</h3>
    <p className="text-3xl font-bold text-gray-900 mt-2">{jobs.length}</p>
  </div>
  <div className="bg-white rounded-lg shadow p-6">
    <h3 className="text-lg font-semibold text-gray-700">Pending</h3>
    <p className="text-3xl font-bold text-yellow-600 mt-2">
      {jobs.filter(job => job.status === 'pending').length}
    </p>
  </div>
  <div className="bg-white rounded-lg shadow p-6">
    <h3 className="text-lg font-semibold text-gray-700">Running</h3>
    <p className="text-3xl font-bold text-blue-600 mt-2">
      {jobs.filter(job => job.status === 'running').length}
    </p>
  </div>
  <div className="bg-white rounded-lg shadow p-6">
    <h3 className="text-lg font-semibold text-gray-700">Completed</h3>
    <p className="text-3xl font-bold text-green-600 mt-2">
      {jobs.filter(job => job.status === 'completed').length}
    </p>
  </div>
  {/* NEW: Webhook Stats Card */}
  <div className="bg-white rounded-lg shadow p-6">
    <h3 className="text-lg font-semibold text-gray-700">Webhooks</h3>
    <p className="text-3xl font-bold text-purple-600 mt-2">
      {stats?.overview?.webhooksSent || 
       jobs.filter(job => job.webhookSent).length}
    </p>
    <p className="text-xs text-gray-500 mt-1">Sent successfully</p>
  </div>
</div>

// In Dashboard.js header section, add:
<div className="flex justify-between items-center">
  <div>
    <h1 className="text-3xl font-bold text-gray-900">Job Dashboard</h1>
    <p className="text-gray-600 mt-1">Manage and monitor your automated jobs</p>
  </div>
  <div className="flex space-x-3">
    <button
      onClick={async () => {
        try {
          toast.loading('Testing webhook...');
          const response = await fetch('/api/test-webhook', { method: 'POST' });
          const data = await response.json();
          toast.dismiss();
          if (data.success) {
            toast.success('Webhook test successful!');
          } else {
            toast.error('Webhook test failed');
          }
        } catch (error) {
          toast.dismiss();
          toast.error('Webhook test error');
        }
      }}
      className="px-4 py-2 bg-purple-600 text-white rounded-lg hover:bg-purple-700 transition"
    >
      Test Webhook
    </button>
    <button
      onClick={handleCreateJob}
      className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition"
    >
      + Create New Job
    </button>
  </div>
</div>
      {/* Debug Info */}
      <div className="bg-gray-50 rounded-lg p-4 text-sm">
        <h3 className="font-medium text-gray-700 mb-2">Debug Information:</h3>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-2">
          <div>
            <span className="text-gray-500">Backend API:</span>
            <span className={`ml-2 ${jobs.length > 0 ? 'text-green-600' : 'text-red-600'}`}>
              {jobs.length > 0 ? '✅ Connected' : '❌ Not connected'}
            </span>
          </div>
          <div>
            <span className="text-gray-500">Jobs loaded:</span>
            <span className="ml-2 font-medium">{jobs.length}</span>
          </div>
          <div>
            <span className="text-gray-500">Backend URL:</span>
            <span className="ml-2 font-mono">http://localhost:5000</span>
          </div>
          <div>
            <span className="text-gray-500">Test API:</span>
            <a 
              href="http://localhost:5000/api/jobs" 
              target="_blank" 
              rel="noreferrer"
              className="ml-2 text-blue-600 hover:underline"
            >
              Click to test
            </a>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
