import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { jobApi } from '../services/api';
import toast from 'react-hot-toast';
import { format } from 'date-fns';

const JobDetails = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [job, setJob] = useState(null);
  const [loading, setLoading] = useState(true);
  const [refreshing, setRefreshing] = useState(false);

  const fetchJob = async () => {
    try {
      const response = await jobApi.getJob(id);
      setJob(response.data);
    } catch (error) {
      console.error('Error fetching job:', error);
      toast.error('Failed to load job details');
      navigate('/dashboard');
    } finally {
      setLoading(false);
      setRefreshing(false);
    }
  };

  useEffect(() => {
    fetchJob();
  }, [id]);

  const handleRunJob = async () => {
    if (!window.confirm('Are you sure you want to run this job?')) return;
    
    try {
      toast.loading('Starting job...');
      await jobApi.runJob(id);
      toast.dismiss();
      toast.success('Job started successfully');
      
      // Start refreshing status
      setRefreshing(true);
      const interval = setInterval(() => {
        fetchJob();
      }, 1000);
      
      // Stop refreshing after 5 seconds
      setTimeout(() => {
        clearInterval(interval);
        setRefreshing(false);
      }, 5000);
    } catch (error) {
      toast.dismiss();
      toast.error(error.response?.data?.error || 'Failed to run job');
    }
  };

  const handleDeleteJob = async () => {
    if (!window.confirm('Are you sure you want to delete this job?')) return;
    
    try {
      await jobApi.deleteJob(id);
      toast.success('Job deleted successfully');
      navigate('/dashboard');
    } catch (error) {
      toast.error('Failed to delete job');
    }
  };

  const getStatusColor = (status) => {
    switch (status) {
      case 'pending': return 'bg-yellow-100 text-yellow-800';
      case 'running': return 'bg-blue-100 text-blue-800';
      case 'completed': return 'bg-green-100 text-green-800';
      case 'failed': return 'bg-red-100 text-red-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const getPriorityColor = (priority) => {
    switch (priority) {
      case 'High': return 'bg-red-100 text-red-800';
      case 'Medium': return 'bg-yellow-100 text-yellow-800';
      case 'Low': return 'bg-green-100 text-green-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center min-h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-blue-500"></div>
      </div>
    );
  }

  if (!job) return null;

  return (
    <div className="max-w-4xl mx-auto">
      <div className="mb-8">
        <button
          onClick={() => navigate('/dashboard')}
          className="text-blue-600 hover:text-blue-800 mb-4 flex items-center"
        >
          ‚Üê Back to Dashboard
        </button>
        <div className="flex justify-between items-center">
          <h1 className="text-3xl font-bold text-gray-900">{job.taskName}</h1>
          <div className="flex space-x-3">
            <button
              onClick={handleRunJob}
              disabled={job.status !== 'pending' || refreshing}
              className="px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition disabled:opacity-50"
            >
              {refreshing ? 'Running...' : 'Run Job'}
            </button>
            <button
              onClick={handleDeleteJob}
              className="px-4 py-2 border border-red-600 text-red-600 rounded-lg hover:bg-red-50 transition"
            >
              Delete
            </button>
          </div>
        </div>
        <p className="text-gray-600 mt-2">Job ID: {job.id}</p>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-6">
        <div className="bg-white rounded-lg shadow p-6">
          <h3 className="text-lg font-semibold text-gray-700 mb-4">Status</h3>
          <div className="flex items-center justify-between">
            <span className={`px-3 py-1 rounded-full text-sm font-medium ${getStatusColor(job.status)}`}>
              {job.status.charAt(0).toUpperCase() + job.status.slice(1)}
            </span>
            {refreshing && (
              <div className="animate-spin rounded-full h-4 w-4 border-t-2 border-b-2 border-blue-500"></div>
            )}
          </div>
        </div>

        <div className="bg-white rounded-lg shadow p-6">
          <h3 className="text-lg font-semibold text-gray-700 mb-4">Priority</h3>
          <span className={`px-3 py-1 rounded-full text-sm font-medium ${getPriorityColor(job.priority)}`}>
            {job.priority}
          </span>
        </div>

        <div className="bg-white rounded-lg shadow p-6">
          <h3 className="text-lg font-semibold text-gray-700 mb-4">Timestamps</h3>
          <div className="space-y-2 text-sm">
            <div className="flex justify-between">
              <span className="text-gray-500">Created:</span>
              <span>{format(new Date(job.createdAt), 'PPpp')}</span>
            </div>
            <div className="flex justify-between">
              <span className="text-gray-500">Updated:</span>
              <span>{format(new Date(job.updatedAt), 'PPpp')}</span>
            </div>
            {job.completedAt && (
              <div className="flex justify-between">
                <span className="text-gray-500">Completed:</span>
                <span>{format(new Date(job.completedAt), 'PPpp')}</span>
              </div>
            )}
          </div>
        </div>
      </div>

      <div className="bg-white rounded-lg shadow p-6">
        <h3 className="text-lg font-semibold text-gray-700 mb-4">Payload Data</h3>
        <pre className="bg-gray-50 p-4 rounded-lg overflow-x-auto">
          <code className="text-sm text-gray-800">
            {JSON.stringify(job.payload ? JSON.parse(job.payload) : {}, null, 2)}
          </code>
        </pre>
        <p className="mt-2 text-sm text-gray-500">
          This JSON data is passed to the job when it runs and sent to webhook upon completion.
        </p>
      </div>
    </div>
  );
};

export default JobDetails;
